import os
import random
import sys
import base64
from config import banner
info = """
. 目录结构 :
├── welcome.py(启动函数)  Start function
├── requirements.txt(依赖库) Dependent library
├── config（配置文件） Profile
│   ├── config.py(参数配置) Parameter configuration
│   ├── entrance.py(入口函数) Entry function
│   └── banner.py(banner) banner
├── output(输出目录) Output directory
│   ├── url1 (域名1) 
│   │   ├──background (后台扫描信息) Scan background information
│   │   ├──subdomain (子域名信息) Subdomain information
│   │   ├──whois (whois信息) Whois information
│   │   └──robots.txt (robots信息) Robots information
│   ├── url2 (域名2) 
│   │   ├──background (后台扫描信息) Scan background information
│   │   ├──subdomain (子域名信息) Subdomain information
│   │   ├──whois (whois信息) Whois information
│   │   └──robots.txt (robots信息) Robots information
├── static(静态资源目录) Static resource directory
│   ├── bg_url_dict.txt (默认后台扫描字典) Default Background Scan Dictionary
│   └── cmsData.json (cms指纹信息) CMS fingerprint information
├── util(功能函数目录)  Function function directory
└── README.md(说明文档) Documentation
"""
print(random.choice(banner.banner))
print("python version = " + sys.version[0:5] + "\n")
print('查看config目录下config.py文件更改配置')

print(info)
# config内容base64编码
CONFIG = ""
ENTRANCE = ""
BANNER = ""

# 检查config目录是否存在,不存在则创建一个
if not os.path.exists(r'./config/'):
    os.mkdir(r'./config/')
    print('已创建config目录')

# config.py不存在则生成默认config.py文件
if not os.path.isfile("config/config.py"):
    with open("config/config.py", "w", encoding="utf-8") as f:
        f.write(str(base64.b64decode(CONFIG), "utf-8"))

# entrance.py不存在则生成entrance.py文件
if not os.path.isfile("config/entrance.py"):
    with open("config/entrance.py", "w", encoding="utf-8") as f:
        f.write(str(base64.b64decode(ENTRANCE), "utf-8"))

# banner.py不存在则生成banner.py文件
if not os.path.isfile("config/banner.py"):
    with open("config/banner.py", "w", encoding="utf-8") as f:
        f.write(str(base64.b64decode(BANNER), "utf-8"))

# 检查output目录是否存在,不存在则创建一个
if not os.path.exists(r'./output/'):
    os.mkdir(r'./output/')
    print('已创建output目录')

# 获得当前config.py的base64
with open("config/config.py", "r", encoding="utf-8") as f:
    ftxt = f.read()
    CONFIG = base64.b64encode(ftxt.encode('utf-8'))
    print("CONFIG = " + "\"" + str(CONFIG)[2:-1] + "\"\n")

# 获得entrance.py的base64
with open("config/entrance.py", "r", encoding="utf-8") as f:
    ftxt = f.read()
    ENTRANCE = base64.b64encode(ftxt.encode('utf-8'))
    print("ENTRANCE = " + "\"" + str(ENTRANCE)[2:-1] + "\"\n")

# 获得当前banner.py的base64
with open("config/banner.py", "r", encoding="utf-8") as f:
    ftxt = f.read()
    BANNER = base64.b64encode(ftxt.encode('utf-8'))
    print("BANNER = " + "\"" + str(BANNER)[2:-1] + "\"\n")